import React, { useState } from 'react';
import '../style/Wither.scss'; 
import Logo from '../assets/logo-fintek.png';

const Weather = () => {
  const [inputValue, setInputValue] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [cityName, setCityName] = useState('תל אביב'); // ערך ברירת מחדל

  const handleSubmit = () => {
    if (!inputValue) {
      setErrorMessage('נא להזין שם עיר.');
      setCityName('תל אביב'); // השתמש בערך ברירת המחדל
    } else if (!/^[a-zA-Z\s]+$/.test(inputValue)) {
      setErrorMessage('השם שהוזן אינו חוקי. יש להזין רק אותיות באנגלית.');
    } else {
      setErrorMessage('');
      setCityName(inputValue); // שמור את שם העיר שהוזן
    }
    setIsSubmitted(true); // הצג את המלבן הטורקיז
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  return (
    <div className="weather-container">
      <div className="weather-content">
        {/* לוגו בפינה השמאלית העליונה */}
        <div className="weather-logo">
          <img src={Logo} alt="Logo" className="logo" />
        </div>
        <div className="weather-box">
          <div className={`weather-info ${isSubmitted ? 'show' : ''}`}>
            <p>Use our weather app to see the weather around the world</p>
            <h5>City name</h5>
            <div className="weather-input-container">
              <input
                type="text"
                placeholder="Enter input..."
                value={inputValue}
                onChange={handleInputChange}
              />
              <button onClick={handleSubmit}>Check</button>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
          </div>
          {isSubmitted && (
            <div className="weather-display">
              <div className="weather-turquoise">
                <p className="city-name">{cityName}</p> {/* הצג את שם העיר */}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Weather;
