import React, { useState, useEffect } from 'react';

const Weather = () => {
  // הגדרת מצב בסיסי (state) עבור תנאי מזג האוויר
  const [weather, setWeather] = useState(null);

  // פונקציה שמביאה את נתוני מזג האוויר (למשל, מממשק API)
  const fetchWeather = async () => {
    try {
      const response = await fetch('https://api.example.com/weather'); // שנה ל-URL אמיתי
      const data = await response.json();
      setWeather(data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  // השפעה צדדית (side effect) להטעין נתוני מזג האוויר כאשר הקומפוננטה נדרסת
  useEffect(() => {
    fetchWeather();
  }, []);

  return (
    <div>
      <h1>Weather Component</h1>
      {weather ? (
        <div>
          <p>Temperature: {weather.temperature}°C</p>
          <p>Condition: {weather.condition}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Weather;
