import React, { useState, useEffect } from 'react';
import '../style/Weather.scss'; 
import Logo from '../assets/logo-fintek.png';
import { getWeather, getCitySuggestions } from '../axios/weather.service'; // הוספת פונקציה לקבלת הצעות לערים

const Weather = () => {
  const [inputValue, setInputValue] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [citySuggestions, setCitySuggestions] = useState([]); // שדה להחזיק את הצעות הערים

  useEffect(() => {
    const fetchCitySuggestions = async () => {
      if (inputValue.length > 2) { // אם האורך של הקלט גדול מ-2 תווים
        try {
          const suggestions = await getCitySuggestions(inputValue); // פונקציה להשלמת העיר
          setCitySuggestions(suggestions);
        } catch (error) {
          console.error('Failed to fetch city suggestions', error);
        }
      } else {
        setCitySuggestions([]);
      }
    };

    fetchCitySuggestions();
  }, [inputValue]);

  const handleSubmit = async () => {
    if (!inputValue) {
      setErrorMessage('נא להזין שם עיר.');
      setWeatherData(null);
      setError(null);
      setIsSubmitted(false);
    } else if (!/^[a-zA-Z\s]+$/.test(inputValue)) {
      setErrorMessage('השם שהוזן אינו חוקי. יש להזין רק אותיות באנגלית.');
      setWeatherData(null);
      setError(null);
      setIsSubmitted(false);
    } else {
      setErrorMessage('');
      setIsSubmitted(false); // הסתרת התצוגה של תוצאות קודמות
      setIsLoading(true); // התחלת טעינה
      setWeatherData(null); // ניקוי נתונים ישנים
      setError(null); // ניקוי הודעות שגיאה

      try {
        const data = await getWeather(inputValue);
        if (data) {
          setWeatherData(data);alert
          (data.temperatures[1]); // תיקון השימוש ב-data במקום weatherData
          setIsSubmitted(true);
        } else {
          setError('No data available for the city');
        }
      } catch (error) {
        setError('Failed to fetch weather data');
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  return (
    <div className="weather-container">
      <div className="weather-content">
        <div className="weather-logo">
          <img src={Logo} alt="Logo" className="logo" />
        </div>
        <div className="weather-box">
          <div className={`weather-info ${isSubmitted || isLoading ? 'hide' : ''}`}>
            <p>Use our weather app <br/> to see the weather <br/> around the world</p>
            <h5>City name</h5>
            <div className="weather-input-container">
              <input
                type="text"
                placeholder="Enter city..."
                value={inputValue}
                onChange={handleInputChange}
                list="city-suggestions" // ריפרנציה לרשימת ההשלמות
              />
              <datalist id="city-suggestions">
                {citySuggestions.map((suggestion, index) => (
                  <option key={index} value={suggestion} />
                ))}
              </datalist>
              <button onClick={handleSubmit}>Check</button>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {isLoading && <p className='note'>Loading...</p>}
            {error && !isLoading && (
              <p className='note'>{error}</p>
            )}
          </div>

          {isSubmitted && weatherData && (
            <div className="weather-display">
              <div className="weather-display weather-turquoise">
                <h2 className="area">{weatherData.city}</h2>
                <h3 className="area">{weatherData.country}</h3>
                <p id='tytle'>{weatherData.dateTime}</p>
                <h1>{weatherData.temperature}°</h1>
                <p>{weatherData.condition}</p>
                <div className="weather-details">
                  <div className="weather-table">
                    <div className="weather-table-header">
                      <div id="tytle" className="weather-table-cell">Precipitation</div>
                      <div id="tytle" className="weather-table-cell">Humidity</div>
                      <div id="tytle" className="weather-table-cell">Wind</div>
                    </div>
                    <div className="weather-table-body">
                      <div className="weather-table-cell">{weatherData.precipitation} mm</div>
                      <div className="weather-table-cell">{weatherData.humidity}%</div>
                      <div className="weather-table-cell">{weatherData.wind} km/h</div>
                    </div>
                  </div>
                </div>

                <div className="temperature-table">
                  <div className="temperature-table-header">
                    {weatherData.temperatures.map(t => (
                      <div id='tytle' key={t.time} className="temperature-table-cell">{t.time}</div>
                    ))}
                  </div>
                  <div className="temperature-table-body">
                    {weatherData.temperatures.map(t => (
                      <div key={t.time} className="temperature-table-cell">{t.temperature}°</div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Weather;
