import React, { useState } from 'react';
import { getWeather } from '../axios/weather.service'; // תיקון בהתאם למיקום הקובץ

const WeatherData = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleFetchWeather = async () => {
    try {
        
      const data = await getWeather(city);
      setWeatherData(data); // עדכון מצב עם נתוני מזג האוויר
      setError(null); // נקה שגיאות
    } catch (error) {
      setWeatherData(null);
      setError('Failed to fetch weather data'); // הגדרת הודעת שגיאה
    }
  };

  return (
    <div>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name"
      />
      <button onClick={handleFetchWeather}>Get Weather</button>
      {weatherData && (
        <div>
          <h2>Weather for {weatherData.location.name}</h2>
          <p>Temperature: {weatherData.current.temp_c}°C</p>
          <p>Condition: {weatherData.current.condition.text}</p>
        </div>
      )}
      {error && <p>{error}</p>}
    </div>
  );
};

export default WeatherData;
