import axios from 'axios';

// יצירת מופע axios עם הגדרות בסיסיות
const apiClient = axios.create({
  baseURL: 'http://localhost:5000/api', // ודא שהפורט נכון
  timeout: 10000, // זמן המתנה למענה
});

// פונקציה לשליפת נתוני מזג האוויר
export const getWeather = async (city) => {
    alert(city)
  try {
    const response = await apiClient.get('/weather', {
      params: { city },
    });
    
    return response.data; // מחזיר את הנתונים שהתקבלו
  } catch (error) {
    console.error('Error fetching weather data:', error);
    throw error; // תזרוק שגיאה במידה ויש בעיה
  }
};
