import React, { useState } from 'react';
import '../style/Wither.scss';
import logoImage from '../assets/logo.png';

const Weather = () => {
  const [inputValue, setInputValue] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = () => {
    if (!inputValue) {
      setErrorMessage('יש להכניס קלט.');
      return;
    } else if (!/^[a-zA-Z\s]+$/.test(inputValue)) {
      setErrorMessage('הקלט אינו תקין. הכנס רק אותיות.');
      return;
    }

    setErrorMessage('');
    setIsSubmitted(true);
  };

  return (
    <div className="weather-container">
      <div className="weather-content">
        <img src={logoImage} alt="Logo" className="weather-logo" />
        <div className="weather-box">
          <div className={`weather-info ${isSubmitted ? 'show' : ''}`}>
            <p>City name</p>
            <div className="weather-input-container">
              <input
                type="text"
                placeholder="הזן קלט..."
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
              />
              <button onClick={handleSubmit}>Check</button>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
          </div>
          {isSubmitted && (
            <div className="weather-display">
              <div className="weather-turquoise"></div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Weather;
